mapper.select:
select i.id, p.post_title, p.post_content, p.created_date, p.updated_date, m.member_nickname, i.inquiry_email, i.inquiry_type, i.inquiry_status from tbl_inquiry i join tbl_post p on i.id = p.id join tbl_member m on p.member_id = m.id order by
p.created_date i.inquiry_status
desc limit #{pagination.startRow}, #{pagination.rowCount};

mapper.update:
update tbl_post
set post_title = #{postTitle},
    post_content = #{postContent}
where id = #{id}
