mapper.select:
select id, member_email, member_star_rate, member_introduction,
       member_name, member_phone, member_password, member_nickname, member_jung,
       member_point, member_type, member_login_type, created_date, updated_date
from tbl_member
where member_email = #{memberEmail}
  and member_password = #{memberPassword}

mapper.update:
update tbl_member
set member_email = #{memberEmail}, member_name = #{memberName}, reset_uuid = #{resetUuid}
where id = #{id}

update tbl_member
set member_password = #{memberPassword}, reset_uuid = NULL
where member_email = #{memberEmail}

mapper.select:
select m.id, m.member_nickname, m.member_introduction, m.member_star_rate, count(p.id), count(r.id) from tbl_post p join ( select m.id, m.member_nickname, count(r.id) from tbl_member m join tbl_review r on m.member_nickname = r.vt_group_name and m.member_type = "ORGANIZATION" group by m.id ) on p.member_id = m.id and p.post_type = "VOLUNTEER" group by m.id order by
m.member_star_rate count(r.id)
desc limit #{pagination.startRow}, #{pagination.rowCount};

select m.id, m.member_nickname, sum(r.vt_time), r.vt_date
from tbl_member m join (
    select r.id, a.member_id, sum(r.vt_time), r.vt_date
    from tbl_vt_application a join tbl_vt_record r
    on a.id = r.id and month(r.vt_date) = #{month}
    group by a.member_id
)
on m.id = a.member_id
group by m.id
order by sum(r.vt_time) desc
limit 5;
