mapper.select:
select v.id, v.vtRecruitmentCount, p.postTitle, m.memberNickName, v.vtSDate, v.vtEDate,p.postViewCount, p.postType, p.postSummary, p.createdDate from tbl_vt v join tbl_post p on v.id = p.post_id join tbl_member m on p.member_id = m.id order by
v.id p.createdDate
desc limit #{pagination.startRow}, #{pagination.rowCount}
